INSERT INTO `jform_cgreport_head` VALUES ('402880e64e1ef94d014e1efefc2a0001','select * from weixin_account','weixin_account','测试多数据源功能','跨数据库报表演示','管理员','2016-03-15 11:34:38','admin',NULL,NULL,NULL,'jeewx-yunying','',''),('402880e64eb9a22c014eb9a4d5890001','select * from t_s_base_user','ddt_s_base_user','全表查询','用户全部查询报表','管理员','2015-12-03 23:03:28','admin',NULL,NULL,NULL,'','',''),('402880e74d76e784014d76f9e783001e','select username account , realname from t_s_base_user','user_msg','用户POPUP，弹出页面','POPUP用户列表','管理员','2015-12-03 23:03:18','admin',NULL,NULL,NULL,'','account','realname'),('402881e6549587740154958fc06e0002','select date_format(create_month,\'%Y-%m\') as c_month,count(*) from t_mc_custom_resource group by date_format(create_month,\'%Y-%m\')','resource_report','按月份统计导入用户资产的数量','资产导入统计','管理员','2016-05-15 17:49:42','admin',NULL,NULL,NULL,'','',''),('402881e857145222015714b5c8ad000d','(select depart.departname as \'行部\',tel_manager as \'客户经理\',count(tel_manager) as \'总数\',\r\n   	sum(IF(bpm_status=\'14\',1,0)) as \'未反馈\',sum(IF(feedback_status=\'拒绝\',1,0)) as \'拒绝\',\r\n   	sum(IF(feedback_status=\'跟进中\',1,0)) as \'跟进中\',sum(IF(feedback_status=\'成功\',1,0)) as \'成功\',\r\n	round(sum(IF(feedback_status=\'成功\',1,0))*100/count(tel_manager),2) as \'成功率\'\r\n   from t_mc_tele tele\r\n   left join `t_s_base_user` usr\r\n   on tele.`tel_manager`=usr.`realname`\r\n   left join `t_s_user_org` uorg\r\n   on usr.id=uorg.`user_id`\r\n   left join t_s_depart depart\r\n   on uorg.`org_id`=depart.id\r\n   where `bpm_status` in (\'14\',\'15\')\r\n   ${dis_date}\r\n   group by `tel_manager`,depart.departname)\r\n   union\r\n   (select \'*总计*\',\'\',count(tel_manager) as \'总数\',\r\n   	sum(IF(bpm_status=\'14\',1,0)) as \'未反馈\',sum(IF(feedback_status=\'拒绝\',1,0)) as \'拒绝\',\r\n   	sum(IF(feedback_status=\'跟进中\',1,0)) as \'跟进中\',sum(IF(feedback_status=\'成功\',1,0)) as \'成功\',\r\n	round(sum(IF(feedback_status=\'成功\',1,0))*100/count(tel_manager),2) as \'成功率\'\r\n   from t_mc_tele tele\r\n   where bpm_status in (\'14\',\'15\') ${dis_date})\r\n   order by 行部 desc,成功率 desc','feedback_sum','反馈统计','反馈统计','管理员','2016-09-11 11:29:55','admin',NULL,NULL,NULL,'','',''),('402881eb5671d6c201567206911d0002','(select depart.departname as \'行部\',customer_service as \'客服姓名\',count(customer_service) as \'总数\',\r\n   sum(IF(marketing_status=\'无法联系\',1,0)) as \'无法联系\',sum(IF(marketing_status=\'无法接通\',1,0)) as \'无法接通\',\r\n   sum(IF(marketing_status=\'有需求\',1,0)) as \'有需求\',sum(IF(marketing_status=\'无需求\',1,0)) as \'无需求\',\r\n   sum(IF(marketing_status=\'待跟进\',1,0)) as \'待跟进\',sum(IF(marketing_status=\'号误\',1,0)) as \'号误\',\r\n   round(sum(IF(marketing_status=\'有需求\',1,0))*100/count(customer_service),2) as \'意向达成率\'\r\n   from t_mc_tele tele\r\n   left join `t_s_base_user` usr\r\n   on tele.`customer_service`=usr.`realname`\r\n   left join `t_s_user_org` uorg\r\n   on usr.id=uorg.`user_id`\r\n   left join t_s_depart depart\r\n   on uorg.`org_id`=depart.id\r\n   where bpm_status in (\'13\',\'14\',\'15\')\r\n   ${dis_date}\r\n   group by customer_service,depart.departname)\r\n   union\r\n   (select \'*总计*\',\'\',count(customer_service) as \'总数\',\r\n   sum(IF(marketing_status=\'无法联系\',1,0)) as \'无法联系\',sum(IF(marketing_status=\'无法接通\',1,0)) as \'无法接通\',\r\n   sum(IF(marketing_status=\'有需求\',1,0)) as \'有需求\',sum(IF(marketing_status=\'无需求\',1,0)) as \'无需求\',\r\n   sum(IF(marketing_status=\'待跟进\',1,0)) as \'待跟进\',sum(IF(marketing_status=\'号误\',1,0)) as \'号误\',\r\n   round(sum(IF(marketing_status=\'有需求\',1,0))*100/count(customer_service),2) as \'意向达成率\'\r\n   from t_mc_tele tele\r\n   where bpm_status in (\'13\',\'14\',\'15\') ${dis_date})\r\n   order by 行部 desc,意向达成率 desc','tele_sum','按照营销状态统计','电话营销统计','管理员','2016-09-01 22:08:51','admin',NULL,NULL,NULL,'','','');
